// 导入 vue
import Vue from 'vue'
// 导入 vuex
import Vuex from 'vuex'
// vuex也是vue的插件, 需要use一下, 进行插件的安装初始化
Vue.use(Vuex)

// 创建仓库 store
const store = new Vuex.Store({
  strict: true,
  state: {
    count: 100,
    title: 'hhh'
  },
  mutations: {
    add (state, n) {
      state.count += n
    },
    sub (state, n) {
      state.count -= n
    },
    changeCount (state, newCount) {
      state.count = newCount
    },
    changeTitle (state, newTitle) {
      state.title = newTitle
    },
    subHh (state, n) {
      state.count -= n
    }
  }
})

// 导出仓库
export default store
